function ({channel, message, token}, {response}, {externalContext}) {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
  };
  const body = {
    channel,
    text: message,
  };
  return axios.post("https://slack.com/api/chat.postMessage", body, config)
    .then((res) => response.next(res.data))
    .catch((err) => adv.onError(err));
}
