async function ({token, channel, text}, {success, error}) {
  try {
    const response = await axios.post('https://slack.com/api/chat.postMessage', {
      channel,
      text,
    }, {
      headers: { 'Authorization': `Bearer ${token}` }
    });

    if (response.data.ok) {
      success.next(true);
    } else {
      error.next(response.data.error);
    }
  } catch (err) {
    error.next(err.message);
  }
}
