async function ({query, connection}, {result}, {externalContext}) {
  const db = externalContext.get("db");
  if (!db) {
    throw new Error("Database connection not found in externalContext");
  }

  try {
    const queryResult = await db.query(query, connection);
    result.next(queryResult);
  } catch (error) {
    throw new Error(`Error executing SQL query: ${error.message}`);
  }
}
