Throttle | value,wait | result | result | value

function ({value, wait}, {result}, {state, onCleanup}) {
  const lastExecution = state.get("lastExecution");
  const now = Date.now();

  if (!lastExecution || now - lastExecution >= wait) {
    result.next(value);
    state.set("lastExecution", now);
  } else {
    const remainingTime = wait - (now - lastExecution);
    const newTimer = setTimeout(() => {
      result.next(value);
      state.set("lastExecution", Date.now());
    }, remainingTime);

    state.set("timer", newTimer);
    onCleanup(() => clearTimeout(newTimer));
  }
}
