Throttle | value,wait | result | result | value
function ({value, wait}, {result}, {state, onCleanup}) {
  const lastExecTime = state.get("lastExecTime");
  const now = Date.now();
  if (!lastExecTime || now - lastExecTime >= wait) {
    result.next(value);
    state.set("lastExecTime", now);
  } else {
    const remainingTime = wait - (now - lastExecTime);
    const timer = setTimeout(() => {
      result.next(value);
      state.set("lastExecTime", Date.now());
    }, remainingTime);
    state.set("timer", timer);
    onCleanup(() => clearTimeout(timer));
  }
}
