function ({item, times, delay}, {output}, {state, onCleanup}) {
  let count = state.get("count") || 0;

  const intervalId = setInterval(() => {
    output.next(item);
    count++;

    if (count >= times) {
      clearInterval(intervalId);
    }
  }, delay);

  state.set("count", count);

  onCleanup(() => clearInterval(intervalId));
}
