async function (inputs, outputs, context) {
    const { token, channel, text } = inputs;
    const { slack } = context.externalContext;
    const { messages } = outputs;

    const result = await slack.chat.postMessage({
      token,
      channel,
      text,
    });

    messages.next(result);
  }
