result:
metadata:
  id: "sendSlackMessage"
  inputs:
    - name: "channel"
      type: "string"
    - name: "message"
      type: "string"
  outputs:
    - name: "success"
      type: "boolean"
    - name: "error"
      type: "string"
  completion: "success"
  reactiveInputs: []

function sendSlackMessage(context: AdvancedContext): void {
  const { channel, message, outputs } = context.inputs;

  // Send message to Slack channel
  slackClient.sendMessage(channel, message)
    .then(() => {
      outputs.success = true;
      context.complete();
    })
    .catch((error) => {
      outputs.error = error.message;
      context.complete();
    });
}
