async function (inputs, outputs, context) {
    const { connection, query } = inputs;
    const { result } = outputs;
    const { state } = context;

    const connectionState = state.get("connection");
    if (!connectionState) {
      throw new Error("No connection");
    }

    const { connection: dbConnection } = connectionState;

    const queryResult = await dbConnection.query(query);
    result.next(queryResult);
  }
