metadata:
  id: "runSqlQuery"
  inputs:
    - name: "query"
      type: "string"
    - name: "connection"
      type: "string"
  outputs:
    - name: "result"
      type: "any"
  completionType: "explicit"
  reactiveInputs:
    - "query"
    - "connection"
function runSqlQuery(context: AdvancedContext): void {
  const { query, connection } = context.inputs;

  // Execute SQL query using the provided connection
  const result = executeSqlQuery(query, connection);

  // Set the result as the output value
  context.outputs.result = result;

  // Complete the part
  context.complete();
}
