function ({value, wait}, {debouncedValue}, {state, onCleanup}) {
  const timer = state.get("timer");
  if (timer) {
    clearTimeout(timer);
  }
  const newTimer = setTimeout(() => {
    debouncedValue.next(value);
  }, wait);

  state.set("timer", newTimer);
  onCleanup(() => clearTimeout(timer));
}
