Throttle | value,wait | result | result | value
function (inputs, outputs, state) {
    const { value, wait } = inputs;
    const { result } = outputs;

    const timer = state.get("timer");
    if (timer) {
      clearTimeout(timer);
    }

    const newTimer = setTimeout(() => {
      result.next(value);
    }, wait);

    state.set("timer", newTimer);
  }
