  "function": "async (inputs, output, context) => {\n    const { value, wait } = inputs;\n    const { state } = context;\n    if (state.timeoutId) {\n      clearTimeout(state.timeoutId);\n    }\n    const timeoutId = setTimeout(() => {\n      output('throttledValue', value);\n    }, wait);\n    context.state.timeoutId = timeoutId;\n  }"
}
