function ({value, wait}, {throttledValue}, {state, onCleanup}) {
  let timer = state.get("timer");
  if (timer) {
    clearTimeout(timer);
  }
  timer = setTimeout(() => {
    throttledValue.next(value);
    state.delete("timer");
  }, wait);

  state.set("timer", timer);
  onCleanup(() => clearTimeout(timer));
}
