Throttle | value, wait | result | result | value
function ({value, wait}, {result}, {state, onCleanup}) {
  const lastValue = state.get("lastValue");
  const lastTime = state.get("lastTime");
  const currentTime = Date.now();

  if (!lastTime || currentTime - lastTime >= wait) {
    result.next(value);
    state.set("lastValue", value);
    state.set("lastTime", currentTime);
  }

  if (!state.get("timer")) {
    const timer = setTimeout(() => {
      const lastValue = state.get("lastValue");
      result.next(lastValue);
      state.set("timer", null);
    }, wait);

    state.set("timer", timer);
    onCleanup(() => clearTimeout(timer));
  }
}
