result:
metadata:
  id: throttle
  inputs:
    - value: any
    - delay: number
  outputs:
    - throttledValue: any
  completion: explicit
  reactive inputs: value

function throttle(context: AdvancedContext) {
  const { value, delay } = context.inputs;
  const { state, onCleanup } = context;

  // Clear any existing timeout
  clearTimeout(state.timeoutId);

  // Create a new timeout to emit the throttled value
  state.timeoutId = setTimeout(() => {
    context.outputs.throttledValue = value;
    context.complete();
  }, delay);

  // Cleanup function to clear the timeout on cleanup
  onCleanup(() => {
    clearTimeout(state.timeoutId);
  });
}
