// 1. Create a new file named "roundRobin.ts"
// 2. Define the following Flyde parts in "roundRobin.ts"

import { createFlyde, createNode } from "flyde";

// Round robin part
const roundRobin = createNode(
  // Metadata
  "roundRobin",
  ["value", "input1", "input2", "input3"],
  ["output1", "output2", "output3"],
  "implicit",
  ["input1", "input2", "input3"],
  // Function
  async (inputs, outputs) => {
    const { value, input1, input2, input3 } = inputs;
    const { output1, output2, output3 } = outputs;

    // Get the current round robin index from the state
    const currentIndex = outputs.state.get("currentIndex") || 0;

    // Determine the next output based on the current index
    let nextOutput;
    if (currentIndex === 0) {
      nextOutput = output1;
    } else if (currentIndex === 1) {
      nextOutput = output2;
    } else {
      nextOutput = output3;
    }

    // Set the value on the next output
    nextOutput.setValue(value);

    // Increment the current index and store it in the state
    const nextIndex = (currentIndex + 1) % 3;
    outputs.state.set("currentIndex", nextIndex);
  }
);

// 3. Export the roundRobin part
export default roundRobin;

// 4. Create a new file named "index.ts"
// 5. Import the roundRobin part from "roundRobin.ts"
import roundRobin from "./roundRobin";

// 6. Create a new Flyde instance
const flyde = createFlyde();

// 7. Add the roundRobin part to the Flyde instance
flyde.addPart(roundRobin);

// 8. Define the inputs and outputs for the roundRobin part
const inputs = {
  value: flyde.createValueInput(),
  input1: flyde.createInput(),
  input2: flyde.createInput(),
  input3: flyde.createInput(),
};
const outputs = {
  output1: flyde.createOutput(),
  output2: flyde.createOutput(),
  output3: flyde.createOutput(),
};

// 9. Connect the inputs and outputs to the roundRobin part
flyde.connect(roundRobin, inputs, outputs);

