import { createFlydPart } from 'flyd-parts';

type SortByAge = (array: { age: number }[]) => { age: number }[];

const sortByAge: SortByAge = (array) =>
  array.sort((a, b) => a.age - b.age);

export const sortArrayByAge = createFlydPart<SortByAge>({
  metadata: {
    inputs: ['array'],
    outputs: ['sortedArray'],
  },
  async *execute(inputs, output, _context) {
    const { array } = inputs;
    const sortedArray = sortByAge(array);
    output.sortedArray(sortedArray);
  },
});
